import React, { useState, useEffect } from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  Container,
  Box,
  Fab,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Button,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Snackbar,
  Alert,
  Avatar,
  Chip
} from '@mui/material';
import {
  Add as AddIcon,
  Assignment as TaskIcon,
  Person as PersonIcon
} from '@mui/icons-material';
import { DragDropContext } from 'react-beautiful-dnd';
import TaskBoard from './components/TaskBoard';
import { taskService } from './services/taskService';
import { v4 as uuidv4 } from 'uuid';

function App() {
  const [tasks, setTasks] = useState([]);
  const [openDialog, setOpenDialog] = useState(false);
  const [newTask, setNewTask] = useState({
    title: '',
    description: '',
    priority: 'medium',
    assignee: 'Hector Mbakama'
  });
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  // Colonnes du tableau Kanban
  const columns = {
    todo: { id: 'todo', title: 'À faire', color: '#1976d2' },
    inProgress: { id: 'inProgress', title: 'En cours', color: '#ed6c02' },
    review: { id: 'review', title: 'En révision', color: '#9c27b0' },
    done: { id: 'done', title: 'Terminé', color: '#2e7d32' }
  };

  useEffect(() => {
    loadTasks();
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  const loadTasks = async () => {
    try {
      const loadedTasks = await taskService.getTasks();
      setTasks(loadedTasks);
    } catch (error) {
      showSnackbar('Erreur lors du chargement des tâches', 'error');
    }
  };

  const handleCreateTask = async () => {
    if (!newTask.title.trim()) {
      showSnackbar('Le titre de la tâche est requis', 'warning');
      return;
    }

    const task = {
      id: uuidv4(),
      ...newTask,
      status: 'todo',
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString()
    };

    try {
      await taskService.createTask(task);
      setTasks(prev => [...prev, task]);
      setOpenDialog(false);
      setNewTask({ title: '', description: '', priority: 'medium', assignee: 'Hector Mbakama' });
      showSnackbar('Tâche créée avec succès', 'success');
    } catch (error) {
      showSnackbar('Erreur lors de la création de la tâche', 'error');
    }
  };

  const handleDragEnd = async (result) => {
    if (!result.destination) return;

    const { source, destination, draggableId } = result;
    
    if (source.droppableId === destination.droppableId) return;

    const updatedTasks = tasks.map(task => 
      task.id === draggableId 
        ? { ...task, status: destination.droppableId, updatedAt: new Date().toISOString() }
        : task
    );

    setTasks(updatedTasks);

    try {
      const task = updatedTasks.find(t => t.id === draggableId);
      await taskService.updateTask(task);
      showSnackbar('Tâche déplacée avec succès', 'success');
    } catch (error) {
      showSnackbar('Erreur lors de la mise à jour', 'error');
      loadTasks(); // Recharger en cas d'erreur
    }
  };

  const handleDeleteTask = async (taskId) => {
    try {
      await taskService.deleteTask(taskId);
      setTasks(prev => prev.filter(task => task.id !== taskId));
      showSnackbar('Tâche supprimée avec succès', 'success');
    } catch (error) {
      showSnackbar('Erreur lors de la suppression', 'error');
    }
  };

  const handleUpdateTask = async (updatedTask) => {
    try {
      await taskService.updateTask(updatedTask);
      setTasks(prev => prev.map(task => 
        task.id === updatedTask.id ? updatedTask : task
      ));
      showSnackbar('Tâche mise à jour avec succès', 'success');
    } catch (error) {
      showSnackbar('Erreur lors de la mise à jour', 'error');
    }
  };

  const showSnackbar = (message, severity) => {
    setSnackbar({ open: true, message, severity });
  };



  return (
    <Box sx={{ flexGrow: 1, minHeight: '100vh', bgcolor: 'background.default' }}>
      <AppBar position="static" elevation={2}>
        <Toolbar>
          <TaskIcon sx={{ mr: 2 }} />
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            TaskFlow - Gestion Collaborative
          </Typography>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <Chip
              avatar={<Avatar><PersonIcon /></Avatar>}
              label="Hector Mbakama"
              variant="outlined"
              sx={{ color: 'white', borderColor: 'white' }}
            />
            <Typography variant="body2">
              {tasks.length} tâche{tasks.length !== 1 ? 's' : ''}
            </Typography>
          </Box>
        </Toolbar>
      </AppBar>

      <Container maxWidth="xl" sx={{ mt: 3, mb: 3 }}>
        <DragDropContext onDragEnd={handleDragEnd}>
          <TaskBoard
            tasks={tasks}
            columns={columns}
            onDeleteTask={handleDeleteTask}
            onUpdateTask={handleUpdateTask}
          />
        </DragDropContext>
      </Container>

      <Fab
        color="primary"
        aria-label="add"
        sx={{ position: 'fixed', bottom: 16, right: 16 }}
        onClick={() => setOpenDialog(true)}
      >
        <AddIcon />
      </Fab>

      <Dialog open={openDialog} onClose={() => setOpenDialog(false)} maxWidth="sm" fullWidth>
        <DialogTitle>Créer une nouvelle tâche</DialogTitle>
        <DialogContent>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 1 }}>
            <TextField
              label="Titre de la tâche"
              fullWidth
              value={newTask.title}
              onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
              required
            />
            <TextField
              label="Description"
              fullWidth
              multiline
              rows={3}
              value={newTask.description}
              onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
            />
            <FormControl fullWidth>
              <InputLabel>Priorité</InputLabel>
              <Select
                value={newTask.priority}
                label="Priorité"
                onChange={(e) => setNewTask({ ...newTask, priority: e.target.value })}
              >
                <MenuItem value="low">Basse</MenuItem>
                <MenuItem value="medium">Moyenne</MenuItem>
                <MenuItem value="high">Haute</MenuItem>
              </Select>
            </FormControl>
            <TextField
              label="Assigné à"
              fullWidth
              value={newTask.assignee}
              onChange={(e) => setNewTask({ ...newTask, assignee: e.target.value })}
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Annuler</Button>
          <Button onClick={handleCreateTask} variant="contained">Créer</Button>
        </DialogActions>
      </Dialog>

      <Snackbar
        open={snackbar.open}
        autoHideDuration={4000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
      >
        <Alert severity={snackbar.severity} onClose={() => setSnackbar({ ...snackbar, open: false })}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
}

export default App;
