import React, { useState, useEffect } from 'react';
import { DragDropContext } from 'react-beautiful-dnd';
import { Modal, Button, Form, Toast, ToastContainer } from 'react-bootstrap';
import TaskBoard from './components/TaskBoard';
import { taskService } from './services/taskService';
import { v4 as uuidv4 } from 'uuid';

function App() {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [newTask, setNewTask] = useState({
    title: '',
    description: '',
    priority: 'medium',
    assignee: 'Hector Mbakama'
  });
  const [toast, setToast] = useState({ show: false, message: '', variant: 'success' });

  // Colonnes du tableau Kanban
  const columns = {
    todo: { id: 'todo', title: 'À faire', color: '#007bff', bgColor: '#007bff' },
    inProgress: { id: 'inProgress', title: 'En cours', color: '#fd7e14', bgColor: '#fd7e14' },
    review: { id: 'review', title: 'En révision', color: '#6f42c1', bgColor: '#6f42c1' },
    done: { id: 'done', title: 'Terminé', color: '#28a745', bgColor: '#28a745' }
  };

  useEffect(() => {
    loadTasks();
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  const loadTasks = async () => {
    try {
      setLoading(true);
      const loadedTasks = await taskService.getTasks();
      setTasks(loadedTasks);
    } catch (error) {
      showToast('Erreur lors du chargement des tâches', 'danger');
    } finally {
      setLoading(false);
    }
  };

  const handleCreateTask = async () => {
    if (!newTask.title.trim()) {
      showToast('Le titre de la tâche est requis', 'warning');
      return;
    }

    const task = {
      id: uuidv4(),
      ...newTask,
      status: 'todo',
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString()
    };

    try {
      await taskService.createTask(task);
      setTasks(prev => [...prev, task]);
      setShowModal(false);
      setNewTask({ title: '', description: '', priority: 'medium', assignee: 'Hector Mbakama' });
      showToast('Tâche créée avec succès', 'success');
    } catch (error) {
      showToast('Erreur lors de la création de la tâche', 'danger');
    }
  };

  const handleDragEnd = async (result) => {
    // Vérifications de sécurité
    if (!result) return;
    if (!result.destination) return;
    if (!result.source) return;

    const { source, destination, draggableId } = result;
    
    // Vérifier que les IDs de colonnes sont valides
    const validColumnIds = Object.keys(columns);
    if (!validColumnIds.includes(source.droppableId) || !validColumnIds.includes(destination.droppableId)) {
      console.error('ID de colonne invalide:', { source: source.droppableId, destination: destination.droppableId });
      return;
    }
    
    // Si même colonne, pas de changement
    if (source.droppableId === destination.droppableId) return;

    // Vérifier que la tâche existe
    const taskExists = tasks.find(task => task.id === draggableId);
    if (!taskExists) {
      console.error('Tâche non trouvée:', draggableId);
      return;
    }

    const updatedTasks = tasks.map(task => 
      task.id === draggableId 
        ? { ...task, status: destination.droppableId, updatedAt: new Date().toISOString() }
        : task
    );

    setTasks(updatedTasks);

    try {
      const task = updatedTasks.find(t => t.id === draggableId);
      await taskService.updateTask(task);
      showToast('Tâche déplacée avec succès', 'success');
    } catch (error) {
      console.error('Erreur lors de la mise à jour:', error);
      showToast('Erreur lors de la mise à jour', 'danger');
      loadTasks(); // Recharger en cas d'erreur
    }
  };

  const handleDeleteTask = async (taskId) => {
    try {
      await taskService.deleteTask(taskId);
      setTasks(prev => prev.filter(task => task.id !== taskId));
      showToast('Tâche supprimée avec succès', 'success');
    } catch (error) {
      showToast('Erreur lors de la suppression', 'danger');
    }
  };

  const handleUpdateTask = async (updatedTask) => {
    try {
      await taskService.updateTask(updatedTask);
      setTasks(prev => prev.map(task => 
        task.id === updatedTask.id ? updatedTask : task
      ));
      showToast('Tâche mise à jour avec succès', 'success');
    } catch (error) {
      showToast('Erreur lors de la mise à jour', 'danger');
    }
  };

  const showToast = (message, variant) => {
    setToast({ show: true, message, variant });
  };

  const handleInputChange = (field, value) => {
    setNewTask(prev => ({ ...prev, [field]: value }));
  };

  return (
    <div className="min-vh-100" style={{ backgroundColor: '#f8f9fa' }}>
      {/* Navigation */}
      <nav className="navbar navbar-expand-lg navbar-dark bg-primary shadow-sm">
        <div className="container-fluid">
          <span className="navbar-brand mb-0 h1">
            <i className="bi bi-kanban me-2"></i>
            TaskFlow - Gestion Collaborative
          </span>
          <div className="d-flex align-items-center text-white">
            <div className="d-flex align-items-center me-4">
              <div className="bg-white rounded-circle p-1 me-2">
                <i className="bi bi-person-fill text-primary"></i>
              </div>
              <span className="me-3">Hector Mbakama</span>
            </div>
            <span className="badge bg-light text-primary">
              {tasks.length} tâche{tasks.length !== 1 ? 's' : ''}
            </span>
          </div>
        </div>
      </nav>

      {/* Contenu principal */}
      <div className="container-fluid py-4">
        {!loading ? (
          <DragDropContext onDragEnd={handleDragEnd}>
            <TaskBoard
              tasks={tasks}
              columns={columns}
              onDeleteTask={handleDeleteTask}
              onUpdateTask={handleUpdateTask}
            />
          </DragDropContext>
        ) : (
          <div className="loading-spinner">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Chargement des tâches...</span>
            </div>
          </div>
        )}
      </div>

      {/* Bouton flottant */}
      <button 
        className="fab"
        onClick={() => setShowModal(true)}
        title="Créer une nouvelle tâche"
      >
        <i className="bi bi-plus-lg"></i>
      </button>

      {/* Modal de création de tâche */}
      <Modal show={showModal} onHide={() => setShowModal(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>
            <i className="bi bi-plus-circle me-2"></i>
            Créer une nouvelle tâche
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Titre de la tâche *</Form.Label>
              <Form.Control
                type="text"
                placeholder="Entrez le titre de la tâche"
                value={newTask.title}
                onChange={(e) => handleInputChange('title', e.target.value)}
                required
              />
            </Form.Group>
            
            <Form.Group className="mb-3">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Description détaillée de la tâche"
                value={newTask.description}
                onChange={(e) => handleInputChange('description', e.target.value)}
              />
            </Form.Group>
            
            <div className="row">
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Priorité</Form.Label>
                  <Form.Select
                    value={newTask.priority}
                    onChange={(e) => handleInputChange('priority', e.target.value)}
                  >
                    <option value="low">🟢 Basse</option>
                    <option value="medium">🟡 Moyenne</option>
                    <option value="high">🔴 Haute</option>
                  </Form.Select>
                </Form.Group>
              </div>
              
              <div className="col-md-6">
                <Form.Group className="mb-3">
                  <Form.Label>Assigné à</Form.Label>
                  <Form.Control
                    type="text"
                    value={newTask.assignee}
                    onChange={(e) => handleInputChange('assignee', e.target.value)}
                  />
                </Form.Group>
              </div>
            </div>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            <i className="bi bi-x-lg me-1"></i>
            Annuler
          </Button>
          <Button variant="primary" onClick={handleCreateTask}>
            <i className="bi bi-check-lg me-1"></i>
            Créer la tâche
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Toast notifications */}
      <ToastContainer className="toast-container">
        <Toast 
          show={toast.show} 
          onClose={() => setToast({ ...toast, show: false })}
          delay={4000}
          autohide
          bg={toast.variant}
        >
          <Toast.Header>
            <i className={`bi bi-${toast.variant === 'success' ? 'check-circle' : toast.variant === 'danger' ? 'exclamation-triangle' : 'info-circle'} me-2`}></i>
            <strong className="me-auto">TaskFlow</strong>
          </Toast.Header>
          <Toast.Body className={toast.variant === 'success' || toast.variant === 'danger' ? 'text-white' : ''}>
            {toast.message}
          </Toast.Body>
        </Toast>
      </ToastContainer>
    </div>
  );
}

export default App;
