import React, { useState, useMemo } from 'react';
import { Container, Row, Col, Card, Badge, Form, InputGroup, Button } from 'react-bootstrap';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';
import { Dropdown as PrimeDropdown } from 'primereact/dropdown';
import { Button as PrimeButton } from 'primereact/button';
import { Tag } from 'primereact/tag';
import { Menu } from 'primereact/menu';
import { useTask } from '../context/TaskContext';

const TaskRegistry = () => {
  const { tasks, loading, deleteTask, updateTask } = useTask();
  const [globalFilter, setGlobalFilter] = useState('');
  const [selectedTasks, setSelectedTasks] = useState([]);
  
  // Options pour les filtres
  const statusOptions = [
    { label: 'Tous les statuts', value: null },
    { label: 'Ã€ faire', value: 'todo' },
    { label: 'En cours', value: 'inProgress' },
    { label: 'En rÃ©vision', value: 'review' },
    { label: 'TerminÃ©', value: 'done' }
  ];
  
  const priorityOptions = [
    { label: 'Toutes prioritÃ©s', value: null },
    { label: 'Haute', value: 'high' },
    { label: 'Moyenne', value: 'medium' },
    { label: 'Basse', value: 'low' }
  ];

  // Filtrage et tri des tÃ¢ches
  const filteredAndSortedTasks = useMemo(() => {
    let filtered = tasks;

    // Recherche textuelle
    if (searchQuery.trim()) {
      const query = searchQuery.toLowerCase();
      filtered = filtered.filter(task => 
        task.title.toLowerCase().includes(query) ||
        task.description.toLowerCase().includes(query) ||
        task.assignee.toLowerCase().includes(query)
      );
    }

    // Filtre par statut
    if (statusFilter !== 'all') {
      filtered = filtered.filter(task => task.status === statusFilter);
    }

    // Filtre par prioritÃ©
    if (priorityFilter !== 'all') {
      filtered = filtered.filter(task => task.priority === priorityFilter);
    }

    // Tri
    filtered.sort((a, b) => {
      let aValue = a[sortBy];
      let bValue = b[sortBy];

      if (sortBy === 'createdAt' || sortBy === 'updatedAt') {
        aValue = new Date(aValue);
        bValue = new Date(bValue);
      }

      if (sortOrder === 'asc') {
        return aValue > bValue ? 1 : -1;
      } else {
        return aValue < bValue ? 1 : -1;
      }
    });

    return filtered;
  }, [tasks, searchQuery, statusFilter, priorityFilter, sortBy, sortOrder]);

  // Pagination
  const totalPages = Math.ceil(filteredAndSortedTasks.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const paginatedTasks = filteredAndSortedTasks.slice(startIndex, startIndex + itemsPerPage);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('fr-FR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getStatusBadge = (status) => {
    const variants = {
      todo: 'primary',
      inProgress: 'warning',
      review: 'info',
      done: 'success'
    };
    const labels = {
      todo: 'Ã€ faire',
      inProgress: 'En cours',
      review: 'En rÃ©vision',
      done: 'TerminÃ©'
    };
    return <Badge bg={variants[status]}>{labels[status]}</Badge>;
  };

  const getPriorityBadge = (priority) => {
    const variants = {
      high: 'danger',
      medium: 'warning',
      low: 'success'
    };
    const labels = {
      high: 'ðŸ”´ Haute',
      medium: 'ðŸŸ¡ Moyenne',
      low: 'ðŸŸ¢ Basse'
    };
    return <Badge bg={variants[priority]}>{labels[priority]}</Badge>;
  };

  const handleSort = (field) => {
    if (sortBy === field) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(field);
      setSortOrder('desc');
    }
    setCurrentPage(1);
  };

  const getSortIcon = (field) => {
    if (sortBy !== field) return <i className="bi bi-arrow-down-up text-muted"></i>;
    return sortOrder === 'asc' ? 
      <i className="bi bi-arrow-up text-primary"></i> : 
      <i className="bi bi-arrow-down text-primary"></i>;
  };

  const handleDelete = async (taskId) => {
    if (window.confirm('ÃŠtes-vous sÃ»r de vouloir supprimer cette tÃ¢che ?')) {
      await deleteTask(taskId);
    }
  };

  const resetFilters = () => {
    setSearchQuery('');
    setStatusFilter('all');
    setPriorityFilter('all');
    setSortBy('updatedAt');
    setSortOrder('desc');
    setCurrentPage(1);
  };

  if (loading) {
    return (
      <Container className="py-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Chargement...</span>
          </div>
        </div>
      </Container>
    );
  }

  return (
    <Container fluid className="py-4">
      {/* En-tÃªte */}
      <Row className="mb-4">
        <Col>
          <h1 className="h3 mb-1">
            <i className="bi bi-list-ul me-2"></i>
            Registre des TÃ¢ches
          </h1>
          <p className="text-muted">Vue complÃ¨te et dÃ©taillÃ©e de toutes vos tÃ¢ches</p>
        </Col>
      </Row>

      {/* Filtres et recherche */}
      <Card className="mb-4 border-0 shadow-sm">
        <Card.Body>
          <Row className="g-3">
            <Col md={4}>
              <InputGroup>
                <InputGroup.Text>
                  <i className="bi bi-search"></i>
                </InputGroup.Text>
                <Form.Control
                  type="text"
                  placeholder="Rechercher dans les tÃ¢ches..."
                  value={searchQuery}
                  onChange={(e) => {
                    setSearchQuery(e.target.value);
                    setCurrentPage(1);
                  }}
                />
              </InputGroup>
            </Col>
            
            <Col md={2}>
              <Form.Select
                value={statusFilter}
                onChange={(e) => {
                  setStatusFilter(e.target.value);
                  setCurrentPage(1);
                }}
              >
                <option value="all">Tous les statuts</option>
                <option value="todo">Ã€ faire</option>
                <option value="inProgress">En cours</option>
                <option value="review">En rÃ©vision</option>
                <option value="done">TerminÃ©</option>
              </Form.Select>
            </Col>
            
            <Col md={2}>
              <Form.Select
                value={priorityFilter}
                onChange={(e) => {
                  setPriorityFilter(e.target.value);
                  setCurrentPage(1);
                }}
              >
                <option value="all">Toutes prioritÃ©s</option>
                <option value="high">Haute</option>
                <option value="medium">Moyenne</option>
                <option value="low">Basse</option>
              </Form.Select>
            </Col>
            
            <Col md={2}>
              <Form.Select
                value={`${sortBy}-${sortOrder}`}
                onChange={(e) => {
                  const [field, order] = e.target.value.split('-');
                  setSortBy(field);
                  setSortOrder(order);
                  setCurrentPage(1);
                }}
              >
                <option value="updatedAt-desc">Plus rÃ©cent</option>
                <option value="updatedAt-asc">Plus ancien</option>
                <option value="title-asc">Titre A-Z</option>
                <option value="title-desc">Titre Z-A</option>
                <option value="priority-desc">PrioritÃ© haute</option>
                <option value="priority-asc">PrioritÃ© basse</option>
              </Form.Select>
            </Col>
            
            <Col md={2}>
              <Button variant="outline-secondary" onClick={resetFilters} className="w-100">
                <i className="bi bi-arrow-clockwise me-1"></i>
                RÃ©initialiser
              </Button>
            </Col>
          </Row>
          
          {/* RÃ©sultats */}
          <div className="mt-3 text-muted">
            <i className="bi bi-info-circle me-1"></i>
            {filteredAndSortedTasks.length} tÃ¢che{filteredAndSortedTasks.length !== 1 ? 's' : ''} trouvÃ©e{filteredAndSortedTasks.length !== 1 ? 's' : ''} sur {tasks.length} au total
          </div>
        </Card.Body>
      </Card>

      {/* Tableau des tÃ¢ches */}
      <Card className="border-0 shadow-sm">
        <Card.Body className="p-0">
          {filteredAndSortedTasks.length > 0 ? (
            <>
              <div className="table-responsive">
                <Table hover className="mb-0">
                  <thead className="bg-light">
                    <tr>
                      <th 
                        className="border-0 cursor-pointer user-select-none"
                        onClick={() => handleSort('title')}
                      >
                        Titre {getSortIcon('title')}
                      </th>
                      <th 
                        className="border-0 cursor-pointer user-select-none"
                        onClick={() => handleSort('status')}
                      >
                        Statut {getSortIcon('status')}
                      </th>
                      <th 
                        className="border-0 cursor-pointer user-select-none"
                        onClick={() => handleSort('priority')}
                      >
                        PrioritÃ© {getSortIcon('priority')}
                      </th>
                      <th 
                        className="border-0 cursor-pointer user-select-none"
                        onClick={() => handleSort('assignee')}
                      >
                        AssignÃ© Ã  {getSortIcon('assignee')}
                      </th>
                      <th 
                        className="border-0 cursor-pointer user-select-none"
                        onClick={() => handleSort('createdAt')}
                      >
                        CrÃ©Ã© le {getSortIcon('createdAt')}
                      </th>
                      <th 
                        className="border-0 cursor-pointer user-select-none"
                        onClick={() => handleSort('updatedAt')}
                      >
                        ModifiÃ© le {getSortIcon('updatedAt')}
                      </th>
                      <th className="border-0">Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {paginatedTasks.map((task) => (
                      <tr key={task.id}>
                        <td>
                          <div>
                            <h6 className="mb-1">{task.title}</h6>
                            {task.description && (
                              <small className="text-muted">{task.description}</small>
                            )}
                          </div>
                        </td>
                        <td>{getStatusBadge(task.status)}</td>
                        <td>{getPriorityBadge(task.priority)}</td>
                        <td>
                          <div className="d-flex align-items-center">
                            <div className="bg-primary rounded-circle me-2" style={{ width: '20px', height: '20px' }}>
                              <i className="bi bi-person-fill text-white" style={{ fontSize: '0.6rem' }}></i>
                            </div>
                            {task.assignee}
                          </div>
                        </td>
                        <td>
                          <small>{formatDate(task.createdAt)}</small>
                        </td>
                        <td>
                          <small>{formatDate(task.updatedAt)}</small>
                        </td>
                        <td>
                          <Dropdown>
                            <Dropdown.Toggle variant="link" size="sm" className="text-muted">
                              <i className="bi bi-three-dots-vertical"></i>
                            </Dropdown.Toggle>
                            <Dropdown.Menu>
                              <Dropdown.Item>
                                <i className="bi bi-pencil me-2"></i>
                                Modifier
                              </Dropdown.Item>
                              <Dropdown.Divider />
                              <Dropdown.Item 
                                className="text-danger"
                                onClick={() => handleDelete(task.id)}
                              >
                                <i className="bi bi-trash me-2"></i>
                                Supprimer
                              </Dropdown.Item>
                            </Dropdown.Menu>
                          </Dropdown>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              </div>

              {/* Pagination */}
              {totalPages > 1 && (
                <div className="d-flex justify-content-center p-3">
                  <Pagination>
                    <Pagination.First 
                      onClick={() => setCurrentPage(1)}
                      disabled={currentPage === 1}
                    />
                    <Pagination.Prev 
                      onClick={() => setCurrentPage(currentPage - 1)}
                      disabled={currentPage === 1}
                    />
                    
                    {[...Array(totalPages)].map((_, index) => {
                      const page = index + 1;
                      if (
                        page === 1 ||
                        page === totalPages ||
                        (page >= currentPage - 1 && page <= currentPage + 1)
                      ) {
                        return (
                          <Pagination.Item
                            key={page}
                            active={page === currentPage}
                            onClick={() => setCurrentPage(page)}
                          >
                            {page}
                          </Pagination.Item>
                        );
                      } else if (
                        page === currentPage - 2 ||
                        page === currentPage + 2
                      ) {
                        return <Pagination.Ellipsis key={page} />;
                      }
                      return null;
                    })}
                    
                    <Pagination.Next 
                      onClick={() => setCurrentPage(currentPage + 1)}
                      disabled={currentPage === totalPages}
                    />
                    <Pagination.Last 
                      onClick={() => setCurrentPage(totalPages)}
                      disabled={currentPage === totalPages}
                    />
                  </Pagination>
                </div>
              )}
            </>
          ) : (
            <div className="text-center py-5">
              <i className="bi bi-inbox display-4 text-muted mb-3"></i>
              <h5 className="text-muted">Aucune tÃ¢che trouvÃ©e</h5>
              <p className="text-muted">Essayez de modifier vos critÃ¨res de recherche</p>
            </div>
          )}
        </Card.Body>
      </Card>
    </Container>
  );
};

export default TaskRegistry;
