import React, { useState } from 'react';
import { Navbar, Nav, Container, Offcanvas, Badge, Toast, ToastContainer } from 'react-bootstrap';
import { Link, useLocation } from 'react-router-dom';
import { useTask } from '../context/TaskContext';
import CreateTaskModal from './CreateTaskModal';

const Layout = ({ children }) => {
  const location = useLocation();
  const { tasks, toast, hideToast, getStats } = useTask();
  const [showOffcanvas, setShowOffcanvas] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false);
  
  const stats = getStats();

  const menuItems = [
    { path: '/', label: 'Dashboard', icon: 'bi-speedometer2' },
    { path: '/kanban', label: 'Tableau Kanban', icon: 'bi-kanban' },
    { path: '/registry', label: 'Registre des TÃ¢ches', icon: 'bi-list-ul' },
    { path: '/analytics', label: 'Analyses', icon: 'bi-graph-up' }
  ];

  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <>
      {/* Navigation principale */}
      <Navbar bg="primary" variant="dark" expand="lg" className="shadow-sm">
        <Container fluid>
          <Navbar.Brand as={Link} to="/" className="d-flex align-items-center">
            <i className="bi bi-kanban me-2"></i>
            <span className="fw-bold">TaskFlow</span>
          </Navbar.Brand>

          {/* Menu burger pour mobile */}
          <button
            className="navbar-toggler d-lg-none"
            type="button"
            onClick={() => setShowOffcanvas(true)}
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          {/* Navigation desktop */}
          <Nav className="me-auto d-none d-lg-flex">
            {menuItems.map((item) => (
              <Nav.Link
                key={item.path}
                as={Link}
                to={item.path}
                className={`d-flex align-items-center px-3 ${isActive(item.path) ? 'active' : ''}`}
              >
                <i className={`${item.icon} me-2`}></i>
                {item.label}
              </Nav.Link>
            ))}
          </Nav>

          {/* Actions et profil */}
          <div className="d-flex align-items-center text-white">
            <button
              className="btn btn-outline-light btn-sm me-3"
              onClick={() => setShowCreateModal(true)}
            >
              <i className="bi bi-plus-lg me-1"></i>
              Nouvelle tÃ¢che
            </button>
            
            <div className="d-flex align-items-center me-4">
              <div className="bg-white rounded-circle p-1 me-2">
                <i className="bi bi-person-fill text-primary"></i>
              </div>
              <div className="d-none d-md-block">
                <div className="fw-semibold">Hector Mbakama</div>
                <small className="opacity-75">Administrateur</small>
              </div>
            </div>
            
            <Badge bg="light" text="primary" className="fs-6">
              {tasks.length} tÃ¢che{tasks.length !== 1 ? 's' : ''}
            </Badge>
          </div>
        </Container>
      </Navbar>

      {/* Menu mobile (Offcanvas) */}
      <Offcanvas show={showOffcanvas} onHide={() => setShowOffcanvas(false)} placement="start">
        <Offcanvas.Header closeButton className="bg-primary text-white">
          <Offcanvas.Title>
            <i className="bi bi-kanban me-2"></i>
            TaskFlow
          </Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body className="p-0">
          <Nav className="flex-column">
            {menuItems.map((item) => (
              <Nav.Link
                key={item.path}
                as={Link}
                to={item.path}
                className={`d-flex align-items-center px-4 py-3 border-bottom ${isActive(item.path) ? 'bg-primary text-white' : ''}`}
                onClick={() => setShowOffcanvas(false)}
              >
                <i className={`${item.icon} me-3`}></i>
                {item.label}
              </Nav.Link>
            ))}
          </Nav>
          
          {/* Statistiques rapides dans le menu mobile */}
          <div className="p-4 bg-light mt-auto">
            <h6 className="fw-bold mb-3">Statistiques rapides</h6>
            <div className="row g-2">
              <div className="col-6">
                <div className="text-center p-2 bg-white rounded">
                  <div className="fw-bold text-primary">{stats.total}</div>
                  <small>Total</small>
                </div>
              </div>
              <div className="col-6">
                <div className="text-center p-2 bg-white rounded">
                  <div className="fw-bold text-success">{stats.done}</div>
                  <small>TerminÃ©es</small>
                </div>
              </div>
            </div>
          </div>
        </Offcanvas.Body>
      </Offcanvas>

      {/* Contenu principal */}
      <main className="min-vh-100" style={{ backgroundColor: '#f8f9fa', paddingTop: '1rem' }}>
        {children}
      </main>

      {/* Bouton flottant pour crÃ©er une tÃ¢che */}
      <button 
        className="fab d-lg-none"
        onClick={() => setShowCreateModal(true)}
        title="CrÃ©er une nouvelle tÃ¢che"
      >
        <i className="bi bi-plus-lg"></i>
      </button>

      {/* Modal de crÃ©ation de tÃ¢che */}
      <CreateTaskModal 
        show={showCreateModal} 
        onHide={() => setShowCreateModal(false)} 
      />

      {/* Toast notifications */}
      <ToastContainer className="toast-container">
        <Toast 
          show={toast.show} 
          onClose={hideToast}
          delay={4000}
          autohide
          bg={toast.variant}
        >
          <Toast.Header>
            <i className={`bi bi-${toast.variant === 'success' ? 'check-circle' : toast.variant === 'danger' ? 'exclamation-triangle' : 'info-circle'} me-2`}></i>
            <strong className="me-auto">TaskFlow</strong>
          </Toast.Header>
          <Toast.Body className={toast.variant === 'success' || toast.variant === 'danger' ? 'text-white' : ''}>
            {toast.message}
          </Toast.Body>
        </Toast>
      </ToastContainer>
    </>
  );
};

export default Layout;
